}

// Build the URL for the Facebook API request based on the provided config, since, and until dates
function buildApiUrl(config, since, until) {
 return `https://graph.facebook.com/${config.PAGE\_ID}/posts?fields=${FIELDS},insights.metric(${INSIGHTS\_METRICS})&period(lifetime)&access\_token=${config.ACCESS\_TOKEN}&limit=${config.LIMIT}&since=${since.toISOString().slice(0, 10)}&until=${until.toISOString().slice(0, 10)}`;
}

// Fetch data from the Facebook API, handling pagination
function fetchDataFromApi(apiUrl, processedIds) {
 let allFetchedData = [];
 let nextPageUrl = apiUrl;
 while (nextPageUrl) {
 const response = UrlFetchApp.fetch(nextPageUrl);
 const json = JSON.parse(response.getContentText());
 allFetchedData = allFetchedData.concat(filterApiResponse(json, processedIds));
 nextPageUrl = json.paging?.next || null;
 }
 return allFetchedData;
}
